if (length >= 12){
  if (ddi == '+'){
    ddi = whatsapp.slice(0,3);
    whatsapp = parseInt(whatsapp.slice(3)).toString();
  } else {
    if (ddx == '55'){
      whatsapp = parseInt(whatsapp.slice(2)).toString();
      ddi = "+55";
    } else {
      whatsapp = parseInt(whatsapp).toString();
      ddi = 'não informado';
    }
  }
} else {
  whatsapp = parseInt(whatsapp).toString();
  ddi = 'não informado';
}

ddd = whatsapp.slice(0,2);
whatsapp = whatsapp.slice(2);
if (ddd > 28){
  whatsapp = whatsapp.slice(-8);
} else {
  whatsapp = '9'+whatsapp.slice(-8);
}
var whatsapp_formatado = '55'+ddd+whatsapp;

return [
  {
    json: {
      ddi,
      ddd,
      whatsapp,
      whatsapp_formatado,   
